import React, { useState, useEffect, useRef } from 'react';
import {
  Box,
  TextField,
  IconButton,
  Typography,
  List,
  ListItem,
  Avatar,
  CircularProgress,
  Chip,
  Divider,
  Button,
  Card,
  CardContent,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Stack,
  alpha,
  useTheme,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PersonIcon from '@mui/icons-material/Person';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import KitchenIcon from '@mui/icons-material/Kitchen';
import SettingsIcon from '@mui/icons-material/Settings';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import WarningAmberIcon from '@mui/icons-material/WarningAmber';
import { sendMessage, getPantryInventory, addPantryItem, deletePantryItem } from '../services/api';

const ChatInterface = () => {
  const theme = useTheme();
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: "ðŸ‘‹ Welcome to Leftovr! I'm your AI chef assistant. I can help you find recipes, manage your pantry, and reduce food waste. What would you like to cook today?",
    },
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Pantry and preferences state
  const [pantryItems, setPantryItems] = useState([]);
  const [preferences, setPreferences] = useState({
    dietary_restrictions: [],
    allergies: [],
    cuisines: [],
    skill_level: 'intermediate',
  });

  // Dialog state
  const [addItemDialogOpen, setAddItemDialogOpen] = useState(false);
  const [newItem, setNewItem] = useState({
    ingredient_name: '',
    quantity: '',
    unit: '',
    expiration_date: '',
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    fetchPantryItems();
  }, []);

  const fetchPantryItems = async () => {
    try {
      const response = await getPantryInventory();
      setPantryItems(response.inventory || []);
    } catch (error) {
      console.error('Error fetching pantry:', error);
    }
  };

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = input.trim();
    setInput('');

    // Add user message
    setMessages((prev) => [...prev, { role: 'user', content: userMessage }]);
    setLoading(true);

    try {
      const response = await sendMessage(userMessage, preferences, pantryItems);

      // Add assistant response
      setMessages((prev) => [
        ...prev,
        {
          role: 'assistant',
          content: response.response || 'Sorry, I encountered an error.',
          data: response,
        },
      ]);

      // Update pantry if changed
      if (response.pantry_inventory) {
        setPantryItems(response.pantry_inventory);
      }
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        {
          role: 'assistant',
          content: 'âŒ Sorry, I encountered an error. Please try again.',
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleAddItem = async () => {
    try {
      await addPantryItem(newItem);
      await fetchPantryItems();
      setAddItemDialogOpen(false);
      setNewItem({ ingredient_name: '', quantity: '', unit: '', expiration_date: '' });
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  const handleDeleteItem = async (itemName) => {
    try {
      await deletePantryItem(itemName);
      await fetchPantryItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handlePreferenceChange = (category, value) => {
    setPreferences((prev) => {
      const current = prev[category] || [];
      const updated = current.includes(value)
        ? current.filter((item) => item !== value)
        : [...current, value];
      return { ...prev, [category]: updated };
    });
  };

  const isExpiringSoon = (expirationDate) => {
    if (!expirationDate) return false;
    const today = new Date();
    const expDate = new Date(expirationDate);
    const daysUntilExpiry = Math.ceil((expDate - today) / (1000 * 60 * 60 * 24));
    return daysUntilExpiry <= 3 && daysUntilExpiry >= 0;
  };

  return (
    <Box sx={{ display: 'flex', height: 'calc(100vh - 64px)', bgcolor: 'background.default' }}>
      {/* Main Chat Area */}
      <Box
        sx={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
        }}
      >
        {/* Messages Area */}
        <Box
          sx={{
            flexGrow: 1,
            overflow: 'auto',
            p: 3,
          }}
        >
          <List sx={{ maxWidth: 900, mx: 'auto' }}>
            {messages.map((message, index) => (
              <ListItem
                key={index}
                sx={{
                  flexDirection: 'column',
                  alignItems: message.role === 'user' ? 'flex-end' : 'flex-start',
                  mb: 2,
                  px: 0,
                }}
              >
                <Box
                  sx={{
                    display: 'flex',
                    gap: 1.5,
                    alignItems: 'flex-start',
                    maxWidth: '85%',
                    flexDirection: message.role === 'user' ? 'row-reverse' : 'row',
                  }}
                >
                  <Avatar
                    sx={{
                      bgcolor: message.role === 'user' 
                        ? theme.palette.secondary.main 
                        : theme.palette.primary.main,
                      width: 36,
                      height: 36,
                    }}
                  >
                    {message.role === 'user' ? <PersonIcon fontSize="small" /> : <SmartToyIcon fontSize="small" />}
                  </Avatar>
                  <Box
                    sx={{
                      p: 2,
                      borderRadius: 2,
                      bgcolor: message.role === 'user' 
                        ? alpha(theme.palette.secondary.main, 0.1)
                        : 'background.paper',
                      border: 1,
                      borderColor: message.role === 'user' 
                        ? alpha(theme.palette.secondary.main, 0.2)
                        : 'divider',
                    }}
                  >
                    <Typography
                      variant="body1"
                      sx={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}
                    >
                      {message.content}
                    </Typography>

                    {/* Display recipe recommendations if available */}
                    {message.data?.top_3_recommendations && (
                      <Box sx={{ mt: 2, display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                        {message.data.top_3_recommendations.map((recipe, idx) => (
                          <Chip
                            key={idx}
                            label={recipe.title}
                            color="primary"
                            variant="outlined"
                          />
                        ))}
                      </Box>
                    )}
                  </Box>
                </Box>
              </ListItem>
            ))}
            {loading && (
              <ListItem sx={{ justifyContent: 'center', px: 0 }}>
                <CircularProgress size={24} />
              </ListItem>
            )}
            <div ref={messagesEndRef} />
          </List>
        </Box>

        {/* Input Area */}
        <Box
          sx={{
            p: 3,
            bgcolor: 'background.paper',
            borderTop: 1,
            borderColor: 'divider',
          }}
        >
          <Box sx={{ display: 'flex', gap: 1.5, maxWidth: 900, mx: 'auto' }}>
            <TextField
              fullWidth
              multiline
              maxRows={4}
              placeholder="Ask me about recipes, ingredients, or cooking tips..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              disabled={loading}
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  borderRadius: 3,
                },
              }}
            />
            <IconButton
              color="primary"
              onClick={handleSend}
              disabled={!input.trim() || loading}
              sx={{
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': {
                  bgcolor: 'primary.dark',
                },
                '&:disabled': {
                  bgcolor: alpha(theme.palette.primary.main, 0.3),
                },
                width: 48,
                height: 48,
              }}
            >
              <SendIcon />
            </IconButton>
          </Box>
        </Box>
      </Box>

    {/* Sidebar Drawer */}
    <Drawer
      anchor="right"
      open={sidebarOpen}
      onClose={() => setSidebarOpen(false)}
      sx={{
        '& .MuiDrawer-paper': {
          width: { xs: '100%', sm: 400 },
          p: 3,
        },
      }}
    >
      <Typography variant="h5" gutterBottom>
        Settings & Pantry
      </Typography>
      <Divider sx={{ mb: 2 }} />

      {/* Preferences Section */}
      <Accordion defaultExpanded>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <SettingsIcon color="primary" />
            <Typography variant="h6">Preferences</Typography>
          </Box>
        </AccordionSummary>
        <AccordionDetails>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {/* Skill Level */}
            <FormControl fullWidth>
              <InputLabel>Skill Level</InputLabel>
              <Select
                value={preferences.skill_level}
                label="Skill Level"
                onChange={(e) => setPreferences({ ...preferences, skill_level: e.target.value })}
              >
                <MenuItem value="beginner">Beginner</MenuItem>
                <MenuItem value="intermediate">Intermediate</MenuItem>
                <MenuItem value="advanced">Advanced</MenuItem>
              </Select>
            </FormControl>

            {/* Dietary Restrictions */}
            <Box>
              <Typography variant="subtitle2" gutterBottom>Dietary Restrictions</Typography>
              <FormGroup>
                {['vegetarian', 'vegan', 'gluten-free', 'dairy-free', 'keto', 'paleo'].map((diet) => (
                  <FormControlLabel
                    key={diet}
                    control={
                      <Checkbox
                        checked={preferences.dietary_restrictions.includes(diet)}
                        onChange={() => handlePreferenceChange('dietary_restrictions', diet)}
                      />
                    }
                    label={diet.charAt(0).toUpperCase() + diet.slice(1)}
                  />
                ))}
              </FormGroup>
            </Box>

            {/* Allergies */}
            <Box>
              <Typography variant="subtitle2" gutterBottom>Allergies</Typography>
              <FormGroup>
                {['nuts', 'shellfish', 'eggs', 'soy', 'wheat'].map((allergy) => (
                  <FormControlLabel
                    key={allergy}
                    control={
                      <Checkbox
                        checked={preferences.allergies.includes(allergy)}
                        onChange={() => handlePreferenceChange('allergies', allergy)}
                      />
                    }
                    label={allergy.charAt(0).toUpperCase() + allergy.slice(1)}
                  />
                ))}
              </FormGroup>
            </Box>
          </Box>
        </AccordionDetails>
      </Accordion>

      {/* Pantry Section */}
      <Accordion defaultExpanded sx={{ mt: 2 }}>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <KitchenIcon color="primary" />
            <Typography variant="h6">Pantry ({pantryItems.length})</Typography>
          </Box>
        </AccordionSummary>
        <AccordionDetails>
          <Box>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              fullWidth
              onClick={() => setAddItemDialogOpen(true)}
              sx={{ mb: 2 }}
            >
              Add Item
            </Button>

            {pantryItems.length === 0 ? (
              <Typography variant="body2" color="text.secondary" textAlign="center">
                No items in pantry
              </Typography>
            ) : (
              <Table size="small">
                <TableBody>
                  {pantryItems.map((item) => (
                    <TableRow key={item.ingredient_name}>
                      <TableCell>
                        <Typography variant="body2">{item.ingredient_name}</Typography>
                        <Typography variant="caption" color="text.secondary">
                          {item.quantity} {item.unit}
                        </Typography>
                      </TableCell>
                      <TableCell align="right">
                        {item.expiration_date && (
                          <Chip
                            label={new Date(item.expiration_date).toLocaleDateString()}
                            size="small"
                            color={
                              new Date(item.expiration_date) < new Date(Date.now() + 3 * 24 * 60 * 60 * 1000)
                                ? 'warning'
                                : 'default'
                            }
                          />
                        )}
                      </TableCell>
                      <TableCell align="right">
                        <IconButton
                          size="small"
                          color="error"
                          onClick={() => handleDeleteItem(item.ingredient_name)}
                        >
                          <DeleteIcon fontSize="small" />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </Box>
        </AccordionDetails>
      </Accordion>
    </Drawer>

    {/* Add Item Dialog */}
    <Dialog open={addItemDialogOpen} onClose={() => setAddItemDialogOpen(false)} maxWidth="sm" fullWidth>
      <DialogTitle>Add Pantry Item</DialogTitle>
      <DialogContent>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
          <TextField
            label="Ingredient Name"
            value={newItem.ingredient_name}
            onChange={(e) => setNewItem({ ...newItem, ingredient_name: e.target.value })}
            fullWidth
          />
          <TextField
            label="Quantity"
            type="number"
            value={newItem.quantity}
            onChange={(e) => setNewItem({ ...newItem, quantity: e.target.value })}
            fullWidth
          />
          <TextField
            label="Unit"
            value={newItem.unit}
            onChange={(e) => setNewItem({ ...newItem, unit: e.target.value })}
            placeholder="e.g., cups, pieces, grams"
            fullWidth
          />
          <TextField
            label="Expiration Date"
            type="date"
            value={newItem.expiration_date}
            onChange={(e) => setNewItem({ ...newItem, expiration_date: e.target.value })}
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setAddItemDialogOpen(false)}>Cancel</Button>
        <Button onClick={handleAddItem} variant="contained">Add</Button>
      </DialogActions>
    </Dialog>
  </Box>
  );
};

export default ChatInterface;
