import React, { useState, useEffect } from 'react';
import {
  Box,
  Drawer,
  Typography,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Divider,
  Chip,
  Collapse,
  FormGroup,
  FormControlLabel,
  Checkbox,
  Radio,
  RadioGroup,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import KitchenIcon from '@mui/icons-material/Kitchen';
import TuneIcon from '@mui/icons-material/Tune';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

// Common dietary restrictions
const DIETARY_RESTRICTIONS = [
  'Vegetarian',
  'Vegan',
  'Pescatarian',
  'Gluten-Free',
  'Dairy-Free',
  'Keto',
  'Paleo',
  'Low-Carb',
  'Halal',
  'Kosher',
];

// Common allergies
const COMMON_ALLERGIES = [
  'Peanuts',
  'Tree Nuts',
  'Shellfish',
  'Fish',
  'Eggs',
  'Milk',
  'Soy',
  'Wheat',
  'Sesame',
];

// Cuisine preferences
const CUISINE_TYPES = [
  'Italian',
  'Mexican',
  'Chinese',
  'Japanese',
  'Indian',
  'Thai',
  'Mediterranean',
  'American',
  'French',
  'Korean',
];

function Sidebar({ open, width, pantryItems, setPantryItems, preferences, setPreferences }) {
  const [newItem, setNewItem] = useState({
    ingredient_name: '',
    quantity: '',
    expire_date: '',
  });
  const [loading, setLoading] = useState(false);
  const [preferencesExpanded, setPreferencesExpanded] = useState(true);
  const [pantryExpanded, setPantryExpanded] = useState(true);
  const [showSuccess, setShowSuccess] = useState(false);

  // Fetch pantry items on mount
  useEffect(() => {
    fetchPantryItems();
  }, []);

  const fetchPantryItems = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/pantry/inventory`);
      setPantryItems(response.data.items || []);
    } catch (err) {
      console.error('Error fetching pantry:', err);
    }
  };

  const handleAddItem = async () => {
    if (!newItem.ingredient_name || !newItem.quantity) {
      return;
    }

    setLoading(true);
    try {
      await axios.post(`${API_BASE_URL}/pantry/add`, {
        ingredient_name: newItem.ingredient_name,
        quantity: parseInt(newItem.quantity),
        expire_date: newItem.expire_date || undefined,
      });

      setNewItem({ ingredient_name: '', quantity: '', expire_date: '' });
      await fetchPantryItems();
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
    } catch (err) {
      console.error('Failed to add item:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteItem = async (itemId) => {
    try {
      await axios.delete(`${API_BASE_URL}/pantry/remove/${itemId}`);
      await fetchPantryItems();
    } catch (err) {
      console.error('Failed to remove item:', err);
    }
  };

  const handleDietaryChange = (restriction) => {
    const current = preferences.dietaryRestrictions || [];
    const updated = current.includes(restriction)
      ? current.filter((r) => r !== restriction)
      : [...current, restriction];
    setPreferences({ ...preferences, dietaryRestrictions: updated });
  };

  const handleAllergyChange = (allergy) => {
    const current = preferences.allergies || [];
    const updated = current.includes(allergy)
      ? current.filter((a) => a !== allergy)
      : [...current, allergy];
    setPreferences({ ...preferences, allergies: updated });
  };

  const handleCuisineChange = (cuisine) => {
    const current = preferences.cuisinePreferences || [];
    const updated = current.includes(cuisine)
      ? current.filter((c) => c !== cuisine)
      : [...current, cuisine];
    setPreferences({ ...preferences, cuisinePreferences: updated });
  };

  const getExpiryStatus = (expireDate) => {
    if (!expireDate) return null;
    const today = new Date();
    const expiry = new Date(expireDate);
    const daysUntilExpiry = Math.ceil((expiry - today) / (1000 * 60 * 60 * 24));

    if (daysUntilExpiry < 0) return { color: '#ef4444', text: 'Expired', icon: '游댮' };
    if (daysUntilExpiry <= 3) return { color: '#f59e0b', text: `${daysUntilExpiry}d`, icon: '游리' };
    return { color: '#10b981', text: `${daysUntilExpiry}d`, icon: '游릭' };
  };

  const getTotalPreferences = () => {
    const dietaryCount = preferences.dietaryRestrictions?.length || 0;
    const allergyCount = preferences.allergies?.length || 0;
    return dietaryCount + allergyCount;
  };

  return (
    <Drawer
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        width: open ? width : 0,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: width,
          boxSizing: 'border-box',
          backgroundColor: '#f9fafb',
          borderRight: '1px solid #e5e7eb',
          overflow: 'hidden',
        },
      }}
    >
      <Box sx={{ display: 'flex', flexDirection: 'column', height: '100vh', overflow: 'hidden' }}>
        {/* Header with Logo */}
        <Box
          sx={{
            p: 3,
            display: 'flex',
            alignItems: 'center',
            gap: 1.5,
            borderBottom: '1px solid #e5e7eb',
            backgroundColor: '#ffffff',
          }}
        >
          <Box
            sx={{
              width: 40,
              height: 40,
              borderRadius: 2,
              backgroundColor: '#10b981',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <RestaurantIcon sx={{ color: '#ffffff', fontSize: 24 }} />
          </Box>
          <Typography variant="h5" sx={{ fontWeight: 700, color: '#1f2937' }}>
            Leftovr
          </Typography>
        </Box>

        {/* Scrollable Content */}
        <Box sx={{ flex: 1, overflowY: 'auto', overflowX: 'hidden', py: 2 }}>

          {/* PREFERENCES SECTION - MOVED TO TOP */}
          <Box sx={{ mb: 2 }}>
            <Box
              sx={{
                px: 3,
                py: 1.5,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                cursor: 'pointer',
                '&:hover': { backgroundColor: '#f3f4f6' },
              }}
              onClick={() => setPreferencesExpanded(!preferencesExpanded)}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
                <TuneIcon sx={{ fontSize: 22, color: '#10b981' }} />
                <Typography variant="h6" sx={{ fontWeight: 600, fontSize: '1rem', color: '#1f2937' }}>
                  My Preferences
                </Typography>
                {getTotalPreferences() > 0 && (
                  <Chip
                    label={getTotalPreferences()}
                    size="small"
                    sx={{
                      height: 20,
                      fontSize: '0.75rem',
                      backgroundColor: '#10b981',
                      color: 'white',
                      fontWeight: 600,
                    }}
                  />
                )}
              </Box>
              {preferencesExpanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
            </Box>

            <Collapse in={preferencesExpanded}>
              <Box sx={{ px: 3, py: 2 }}>

                {/* Dietary Restrictions */}
                <Box sx={{ mb: 3 }}>
                  <Typography
                    variant="subtitle2"
                    sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
                  >
                    游꺔 Dietary Restrictions
                  </Typography>
                  <FormGroup>
                    {DIETARY_RESTRICTIONS.map((restriction) => (
                      <FormControlLabel
                        key={restriction}
                        control={
                          <Checkbox
                            size="small"
                            checked={preferences.dietaryRestrictions?.includes(restriction) || false}
                            onChange={() => handleDietaryChange(restriction)}
                            sx={{
                              color: '#9ca3af',
                              '&.Mui-checked': { color: '#10b981' },
                            }}
                          />
                        }
                        label={
                          <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                            {restriction}
                          </Typography>
                        }
                        sx={{ mb: 0.5 }}
                      />
                    ))}
                  </FormGroup>
                </Box>

                <Divider sx={{ my: 2 }} />

                {/* Allergies */}
                <Box sx={{ mb: 3 }}>
                  <Typography
                    variant="subtitle2"
                    sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
                  >
                    丘멆잺 Allergies
                  </Typography>
                  <FormGroup>
                    {COMMON_ALLERGIES.map((allergy) => (
                      <FormControlLabel
                        key={allergy}
                        control={
                          <Checkbox
                            size="small"
                            checked={preferences.allergies?.includes(allergy) || false}
                            onChange={() => handleAllergyChange(allergy)}
                            sx={{
                              color: '#9ca3af',
                              '&.Mui-checked': { color: '#ef4444' },
                            }}
                          />
                        }
                        label={
                          <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                            {allergy}
                          </Typography>
                        }
                        sx={{ mb: 0.5 }}
                      />
                    ))}
                  </FormGroup>
                </Box>

                <Divider sx={{ my: 2 }} />

                {/* Cuisine Preferences */}
                <Box sx={{ mb: 3 }}>
                  <Typography
                    variant="subtitle2"
                    sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
                  >
                    游꼪 Favorite Cuisines
                  </Typography>
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.75 }}>
                    {CUISINE_TYPES.map((cuisine) => (
                      <Chip
                        key={cuisine}
                        label={cuisine}
                        size="small"
                        onClick={() => handleCuisineChange(cuisine)}
                        variant={preferences.cuisinePreferences?.includes(cuisine) ? 'filled' : 'outlined'}
                        sx={{
                          fontSize: '0.75rem',
                          backgroundColor: preferences.cuisinePreferences?.includes(cuisine) ? '#10b981' : 'transparent',
                          color: preferences.cuisinePreferences?.includes(cuisine) ? 'white' : '#6b7280',
                          borderColor: '#d1d5db',
                          '&:hover': {
                            backgroundColor: preferences.cuisinePreferences?.includes(cuisine) ? '#059669' : '#f3f4f6',
                          },
                        }}
                      />
                    ))}
                  </Box>
                </Box>

                <Divider sx={{ my: 2 }} />

                {/* Skill Level */}
                <Box>
                  <Typography
                    variant="subtitle2"
                    sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
                  >
                    游녿꽳릜 Cooking Skill Level
                  </Typography>
                  <RadioGroup
                    value={preferences.skillLevel || 'intermediate'}
                    onChange={(e) => setPreferences({ ...preferences, skillLevel: e.target.value })}
                  >
                    <FormControlLabel
                      value="beginner"
                      control={<Radio size="small" sx={{ '&.Mui-checked': { color: '#10b981' } }} />}
                      label={<Typography variant="body2" sx={{ fontSize: '0.875rem' }}>Beginner - Simple recipes</Typography>}
                    />
                    <FormControlLabel
                      value="intermediate"
                      control={<Radio size="small" sx={{ '&.Mui-checked': { color: '#10b981' } }} />}
                      label={<Typography variant="body2" sx={{ fontSize: '0.875rem' }}>Intermediate - Moderate complexity</Typography>}
                    />
                    <FormControlLabel
                      value="advanced"
                      control={<Radio size="small" sx={{ '&.Mui-checked': { color: '#10b981' } }} />}
                      label={<Typography variant="body2" sx={{ fontSize: '0.875rem' }}>Advanced - Complex techniques</Typography>}
                    />
                  </RadioGroup>
                </Box>
              </Box>
            </Collapse>
          </Box>

          <Divider sx={{ my: 1 }} />

          {/* PANTRY SECTION */}
          <Box sx={{ mb: 2 }}>
            <Box
              sx={{
                px: 3,
                py: 1.5,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                cursor: 'pointer',
                '&:hover': { backgroundColor: '#f3f4f6' },
              }}
              onClick={() => setPantryExpanded(!pantryExpanded)}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
                <KitchenIcon sx={{ fontSize: 22, color: '#10b981' }} />
                <Typography variant="h6" sx={{ fontWeight: 600, fontSize: '1rem', color: '#1f2937' }}>
                  My Pantry
                </Typography>
                <Chip
                  label={pantryItems.length}
                  size="small"
                  sx={{
                    height: 20,
                    fontSize: '0.75rem',
                    backgroundColor: pantryItems.length > 0 ? '#10b981' : '#9ca3af',
                    color: 'white',
                    fontWeight: 600,
                  }}
                />
              </Box>
              {pantryExpanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
            </Box>

            <Collapse in={pantryExpanded}>
              <Box sx={{ px: 3, py: 2 }}>
                {/* Add Item Form */}
                <Box sx={{ mb: 2, p: 2, backgroundColor: '#ffffff', borderRadius: 2, border: '1px solid #e5e7eb' }}>
                  <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1.5, color: '#374151' }}>
                    Add Ingredient
                  </Typography>
                  <TextField
                    fullWidth
                    size="small"
                    placeholder="e.g., Chicken breast"
                    value={newItem.ingredient_name}
                    onChange={(e) => setNewItem({ ...newItem, ingredient_name: e.target.value })}
                    sx={{ mb: 1 }}
                    InputProps={{
                      sx: { backgroundColor: '#f9fafb' }
                    }}
                  />
                  <Box sx={{ display: 'flex', gap: 1, mb: 1.5 }}>
                    <TextField
                      size="small"
                      placeholder="Quantity"
                      type="number"
                      value={newItem.quantity}
                      onChange={(e) => setNewItem({ ...newItem, quantity: e.target.value })}
                      sx={{ flex: 1 }}
                      InputProps={{
                        sx: { backgroundColor: '#f9fafb' }
                      }}
                    />
                    <TextField
                      size="small"
                      type="date"
                      label="Expiry"
                      value={newItem.expire_date}
                      onChange={(e) => setNewItem({ ...newItem, expire_date: e.target.value })}
                      sx={{ flex: 1 }}
                      InputLabelProps={{ shrink: true }}
                      InputProps={{
                        sx: { backgroundColor: '#f9fafb' }
                      }}
                    />
                  </Box>
                  <Button
                    fullWidth
                    size="medium"
                    variant="contained"
                    startIcon={showSuccess ? <CheckCircleIcon /> : <AddIcon />}
                    onClick={handleAddItem}
                    disabled={loading || !newItem.ingredient_name || !newItem.quantity}
                    sx={{
                      textTransform: 'none',
                      fontWeight: 600,
                      backgroundColor: showSuccess ? '#10b981' : '#10b981',
                      '&:hover': { backgroundColor: '#059669' },
                    }}
                  >
                    {showSuccess ? 'Added!' : 'Add to Pantry'}
                  </Button>
                </Box>

                {/* Pantry Items List */}
                {pantryItems.length === 0 ? (
                  <Box
                    sx={{
                      py: 4,
                      textAlign: 'center',
                      color: '#9ca3af',
                    }}
                  >
                    <KitchenIcon sx={{ fontSize: 48, mb: 1, opacity: 0.3 }} />
                    <Typography variant="body2">
                      Your pantry is empty
                    </Typography>
                    <Typography variant="caption">
                      Add ingredients to get started
                    </Typography>
                  </Box>
                ) : (
                  <List dense sx={{ pt: 0 }}>
                    {pantryItems.map((item) => {
                      const expiryStatus = getExpiryStatus(item.expire_date);
                      return (
                        <ListItem
                          key={item.id}
                          sx={{
                            px: 1.5,
                            py: 1,
                            borderRadius: 1.5,
                            mb: 0.75,
                            backgroundColor: '#ffffff',
                            border: '1px solid #e5e7eb',
                            '&:hover': {
                              backgroundColor: '#f9fafb',
                              '& .delete-icon': { opacity: 1 },
                            },
                          }}
                        >
                          <ListItemText
                            primary={
                              <Typography variant="body2" sx={{ fontWeight: 500, color: '#1f2937' }}>
                                {item.name || item.ingredient_name}
                              </Typography>
                            }
                            secondary={
                              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 0.5 }}>
                                <Typography component="span" variant="caption" sx={{ color: '#6b7280' }}>
                                  Qty: {item.quantity}
                                </Typography>
                                {expiryStatus && (
                                  <Chip
                                    label={`${expiryStatus.icon} ${expiryStatus.text}`}
                                    size="small"
                                    sx={{
                                      height: 18,
                                      fontSize: '0.65rem',
                                      fontWeight: 600,
                                      backgroundColor: `${expiryStatus.color}15`,
                                      color: expiryStatus.color,
                                    }}
                                  />
                                )}
                              </Box>
                            }
                          />
                          <IconButton
                            className="delete-icon"
                            size="small"
                            onClick={() => handleDeleteItem(item.id)}
                            sx={{
                              opacity: 0,
                              transition: 'opacity 0.2s',
                              color: '#ef4444',
                            }}
                          >
                            <DeleteIcon fontSize="small" />
                          </IconButton>
                        </ListItem>
                      );
                    })}
                  </List>
                )}
              </Box>
            </Collapse>
          </Box>
        </Box>
      </Box>
    </Drawer>
  );
}

export default Sidebar;
