import React, { useState } from 'react';
import {
  Container,
  Paper,
  Box,
  Typography,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  Chip,
  CircularProgress,
  Link,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import { searchRecipes } from '../services/api';

const RecipeSearch = () => {
  const [query, setQuery] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  const handleSearch = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSearched(true);
    try {
      const response = await searchRecipes(query);
      setRecipes(response.recipes || []);
    } catch (error) {
      console.error('Search failed:', error);
      setRecipes([]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 3, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Recipe Search
        </Typography>
        <Typography variant="body2" color="text.secondary" paragraph>
          Search for recipes based on ingredients, cuisine, or dietary preferences
        </Typography>

        <Box sx={{ display: 'flex', gap: 2, mt: 3 }}>
          <TextField
            fullWidth
            placeholder="e.g., chicken pasta, vegan dessert, gluten-free..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyPress={handleKeyPress}
            variant="outlined"
          />
          <Button
            variant="contained"
            startIcon={<SearchIcon />}
            onClick={handleSearch}
            disabled={loading || !query.trim()}
            sx={{ minWidth: 120 }}
          >
            Search
          </Button>
        </Box>
      </Paper>

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
          <CircularProgress />
        </Box>
      )}

      {!loading && searched && recipes.length === 0 && (
        <Paper elevation={2} sx={{ p: 4, textAlign: 'center' }}>
          <Typography variant="h6" color="text.secondary">
            No recipes found. Try a different search term.
          </Typography>
        </Paper>
      )}

      {!loading && recipes.length > 0 && (
        <Grid container spacing={3}>
          {recipes.map((recipe, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card elevation={2} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" component="h2" gutterBottom>
                    {recipe.title}
                  </Typography>

                  {recipe.match_percentage && (
                    <Chip
                      label={`${recipe.match_percentage}% match`}
                      color="primary"
                      size="small"
                      sx={{ mb: 1 }}
                    />
                  )}

                  {recipe.source && (
                    <Typography variant="caption" color="text.secondary" display="block" gutterBottom>
                      Source: {recipe.source}
                    </Typography>
                  )}

                  {recipe.recommendation_reason && (
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      ðŸ’¡ {recipe.recommendation_reason}
                    </Typography>
                  )}

                  {recipe.ner && recipe.ner.length > 0 && (
                    <Box sx={{ mt: 2 }}>
                      <Typography variant="caption" color="text.secondary" display="block" gutterBottom>
                        Key Ingredients:
                      </Typography>
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                        {recipe.ner.slice(0, 5).map((ingredient, idx) => (
                          <Chip
                            key={idx}
                            label={ingredient}
                            size="small"
                            variant="outlined"
                          />
                        ))}
                        {recipe.ner.length > 5 && (
                          <Chip
                            label={`+${recipe.ner.length - 5} more`}
                            size="small"
                            variant="outlined"
                          />
                        )}
                      </Box>
                    </Box>
                  )}
                </CardContent>

                <CardActions>
                  {recipe.link && (
                    <Button
                      size="small"
                      endIcon={<OpenInNewIcon />}
                      component={Link}
                      href={recipe.link.startsWith('http') ? recipe.link : `https://${recipe.link}`}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      View Recipe
                    </Button>
                  )}
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default RecipeSearch;
