import React, { useState, useEffect } from 'react';
import {
  Box,
  Drawer,
  Typography,
  Divider,
  Chip,
  FormGroup,
  FormControlLabel,
  Checkbox,
  Radio,
  RadioGroup,
  Button,
  Tooltip,
} from '@mui/material';
import TuneIcon from '@mui/icons-material/Tune';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import BookmarkBorderIcon from '@mui/icons-material/BookmarkBorder';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

// Common dietary restrictions
const DIETARY_RESTRICTIONS = [
  'Vegetarian',
  'Vegan',
  'Pescatarian',
  'Gluten-Free',
  'Dairy-Free',
  'Keto',
  'Paleo',
  'Low-Carb',
  'Halal',
  'Kosher',
];

// Common allergies
const COMMON_ALLERGIES = [
  'Peanuts',
  'Tree Nuts',
  'Shellfish',
  'Fish',
  'Eggs',
  'Milk',
  'Soy',
  'Wheat',
  'Sesame',
];

// Cuisine preferences
const CUISINE_TYPES = [
  'Italian',
  'Mexican',
  'Chinese',
  'Japanese',
  'Indian',
  'Thai',
  'Mediterranean',
  'American',
  'French',
  'Korean',
];

function Sidebar({ open, width, pantryItems, preferences, setPreferences }) {
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);

  const handleDietaryChange = (restriction) => {
    const current = preferences.dietaryRestrictions || [];
    const updated = current.includes(restriction)
      ? current.filter((r) => r !== restriction)
      : [...current, restriction];
    setPreferences({ ...preferences, dietaryRestrictions: updated });
    setHasUnsavedChanges(true);
  };

  const handleAllergyChange = (allergy) => {
    const current = preferences.allergies || [];
    const updated = current.includes(allergy)
      ? current.filter((a) => a !== allergy)
      : [...current, allergy];
    setPreferences({ ...preferences, allergies: updated });
    setHasUnsavedChanges(true);
  };

  const handleCuisineChange = (cuisine) => {
    const current = preferences.cuisinePreferences || [];
    const updated = current.includes(cuisine)
      ? current.filter((c) => c !== cuisine)
      : [...current, cuisine];
    setPreferences({ ...preferences, cuisinePreferences: updated });
    setHasUnsavedChanges(true);
  };

  const getTotalPreferences = () => {
    const dietaryCount = preferences.dietaryRestrictions?.length || 0;
    const allergyCount = preferences.allergies?.length || 0;
    const cuisineCount = preferences.cuisinePreferences?.length || 0;
    return dietaryCount + allergyCount + cuisineCount;
  };

  const handleSavePreferences = async () => {
    // In a real app, you'd save to backend here
    console.log('Saving preferences:', preferences);
    setHasUnsavedChanges(false);
  };

  return (
    <Drawer
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        width: open ? width : 0,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: width,
          boxSizing: 'border-box',
          backgroundColor: '#f9fafb',
          borderRight: '1px solid #e5e7eb',
          overflow: 'hidden',
        },
      }}
    >
      <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%', overflow: 'hidden' }}>
        {/* Header */}
        <Box
          sx={{
            p: 2.5,
            borderBottom: '1px solid #e5e7eb',
            backgroundColor: '#ffffff',
          }}
        >
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5, mb: 2 }}>
            <TuneIcon sx={{ fontSize: 22, color: '#10b981' }} />
            <Typography variant="h6" sx={{ fontWeight: 600, fontSize: '1rem', color: '#1f2937' }}>
              My Preferences
            </Typography>
            {getTotalPreferences() > 0 && (
              <Chip
                label={getTotalPreferences()}
                size="small"
                sx={{
                  height: 20,
                  fontSize: '0.75rem',
                  backgroundColor: '#10b981',
                  color: 'white',
                  fontWeight: 600,
                }}
              />
            )}
          </Box>
          <Typography variant="caption" sx={{ color: '#6b7280', display: 'block' }}>
            Set your dietary preferences to get personalized recipe recommendations
          </Typography>
        </Box>

        {/* Scrollable Content */}
        <Box sx={{ flex: 1, overflowY: 'auto', overflowX: 'hidden', p: 2.5 }}>

          {/* Quick Stats */}
          {pantryItems && pantryItems.length > 0 && (
            <Box
              sx={{
                mb: 3,
                p: 2,
                borderRadius: 2,
                backgroundColor: '#ffffff',
                border: '1px solid #e5e7eb',
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
                <BookmarkBorderIcon sx={{ fontSize: 18, color: '#6b7280' }} />
                <Typography variant="subtitle2" sx={{ fontWeight: 600, color: '#374151' }}>
                  Your Pantry
                </Typography>
              </Box>
              <Typography variant="h4" sx={{ fontWeight: 700, color: '#10b981' }}>
                {pantryItems.length}
              </Typography>
              <Typography variant="caption" sx={{ color: '#6b7280' }}>
                ingredients ready to cook
              </Typography>
            </Box>
          )}

          {/* Dietary Restrictions */}
          <Box sx={{ mb: 3 }}>
            <Typography
              variant="subtitle2"
              sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
            >
              üå± Dietary Restrictions
            </Typography>
            <FormGroup>
              {DIETARY_RESTRICTIONS.map((restriction) => (
                <FormControlLabel
                  key={restriction}
                  control={
                    <Checkbox
                      size="small"
                      checked={preferences.dietaryRestrictions?.includes(restriction) || false}
                      onChange={() => handleDietaryChange(restriction)}
                      sx={{
                        color: '#9ca3af',
                        '&.Mui-checked': { color: '#10b981' },
                      }}
                    />
                  }
                  label={
                    <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                      {restriction}
                    </Typography>
                  }
                  sx={{ mb: 0.5 }}
                />
              ))}
            </FormGroup>
          </Box>

          <Divider sx={{ my: 2 }} />

          {/* Allergies */}
          <Box sx={{ mb: 3 }}>
            <Typography
              variant="subtitle2"
              sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
            >
              ‚ö†Ô∏è Allergies
            </Typography>
            <FormGroup>
              {COMMON_ALLERGIES.map((allergy) => (
                <FormControlLabel
                  key={allergy}
                  control={
                    <Checkbox
                      size="small"
                      checked={preferences.allergies?.includes(allergy) || false}
                      onChange={() => handleAllergyChange(allergy)}
                      sx={{
                        color: '#9ca3af',
                        '&.Mui-checked': { color: '#ef4444' },
                      }}
                    />
                  }
                  label={
                    <Typography variant="body2" sx={{ fontSize: '0.875rem' }}>
                      {allergy}
                    </Typography>
                  }
                  sx={{ mb: 0.5 }}
                />
              ))}
            </FormGroup>
          </Box>

          <Divider sx={{ my: 2 }} />

          {/* Cuisine Preferences */}
          <Box sx={{ mb: 3 }}>
            <Typography
              variant="subtitle2"
              sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
            >
              üçú Favorite Cuisines
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.75 }}>
              {CUISINE_TYPES.map((cuisine) => (
                <Chip
                  key={cuisine}
                  label={cuisine}
                  size="small"
                  onClick={() => handleCuisineChange(cuisine)}
                  variant={preferences.cuisinePreferences?.includes(cuisine) ? 'filled' : 'outlined'}
                  sx={{
                    fontSize: '0.75rem',
                    backgroundColor: preferences.cuisinePreferences?.includes(cuisine) ? '#10b981' : 'transparent',
                    color: preferences.cuisinePreferences?.includes(cuisine) ? 'white' : '#6b7280',
                    borderColor: '#d1d5db',
                    '&:hover': {
                      backgroundColor: preferences.cuisinePreferences?.includes(cuisine) ? '#059669' : '#f3f4f6',
                    },
                  }}
                />
              ))}
            </Box>
          </Box>

          <Divider sx={{ my: 2 }} />

          {/* Skill Level */}
          <Box sx={{ mb: 3 }}>
            <Typography
              variant="subtitle2"
              sx={{ fontWeight: 600, color: '#374151', mb: 1.5, display: 'flex', alignItems: 'center', gap: 1 }}
            >
              üë®‚Äçüç≥ Cooking Skill Level
            </Typography>
            <RadioGroup
              value={preferences.skillLevel || 'intermediate'}
              onChange={(e) => {
                setPreferences({ ...preferences, skillLevel: e.target.value });
                setHasUnsavedChanges(true);
              }}
            >
              <FormControlLabel
                value="beginner"
                control={<Radio size="small" sx={{ '&.Mui-checked': { color: '#10b981' } }} />}
                label={<Typography variant="body2" sx={{ fontSize: '0.875rem' }}>Beginner - Simple recipes</Typography>}
              />
              <FormControlLabel
                value="intermediate"
                control={<Radio size="small" sx={{ '&.Mui-checked': { color: '#10b981' } }} />}
                label={<Typography variant="body2" sx={{ fontSize: '0.875rem' }}>Intermediate - Moderate complexity</Typography>}
              />
              <FormControlLabel
                value="advanced"
                control={<Radio size="small" sx={{ '&.Mui-checked': { color: '#10b981' } }} />}
                label={<Typography variant="body2" sx={{ fontSize: '0.875rem' }}>Advanced - Complex techniques</Typography>}
              />
            </RadioGroup>
          </Box>
        </Box>

        {/* Footer with Save Button */}
        {hasUnsavedChanges && (
          <Box
            sx={{
              p: 2.5,
              borderTop: '1px solid #e5e7eb',
              backgroundColor: '#ffffff',
            }}
          >
            <Button
              fullWidth
              variant="contained"
              onClick={handleSavePreferences}
              sx={{
                textTransform: 'none',
                fontWeight: 600,
                py: 1.25,
              }}
            >
              Save Preferences
            </Button>
          </Box>
        )}
      </Box>
    </Drawer>
  );
}

export default Sidebar;
