  MenuItem,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Stack,
  alpha,
  useTheme,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PersonIcon from '@mui/icons-material/Person';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import KitchenIcon from '@mui/icons-material/Kitchen';
import SettingsIcon from '@mui/icons-material/Settings';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import WarningAmberIcon from '@mui/icons-material/WarningAmber';
import { sendMessage, getPantryInventory, addPantryItem, deletePantryItem } from '../services/api';

const ChatInterface = () => {
  const theme = useTheme();
  const [messages, setMessages] = useState([
    {
      role: 'assistant',
      content: "ðŸ‘‹ Welcome to Leftovr! I'm your AI chef assistant. I can help you find recipes, manage your pantry, and reduce food waste. What would you like to cook today?",
    },
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Pantry and preferences state
  const [pantryItems, setPantryItems] = useState([]);
  const [preferences, setPreferences] = useState({
    dietary_restrictions: [],
    allergies: [],
    cuisines: [],
    skill_level: 'intermediate',
  });

  // Dialog state
  const [addItemDialogOpen, setAddItemDialogOpen] = useState(false);
  const [newItem, setNewItem] = useState({
    ingredient_name: '',
    quantity: '',
    unit: '',
    expiration_date: '',
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    fetchPantryItems();
  }, []);

  const fetchPantryItems = async () => {
    try {
      const response = await getPantryInventory();
      setPantryItems(response.inventory || []);
    } catch (error) {
      console.error('Error fetching pantry:', error);
    }
  };

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = input.trim();
    setInput('');

    // Add user message
    setMessages((prev) => [...prev, { role: 'user', content: userMessage }]);
    setLoading(true);

    try {
      const response = await sendMessage(userMessage, preferences, pantryItems);

      // Add assistant response
      setMessages((prev) => [
        ...prev,
        {
          role: 'assistant',
          content: response.response || 'Sorry, I encountered an error.',
          data: response,
        },
      ]);

      // Update pantry if changed
      if (response.pantry_inventory) {
        setPantryItems(response.pantry_inventory);
      }
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        {
          role: 'assistant',
          content: 'âŒ Sorry, I encountered an error. Please try again.',
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleAddItem = async () => {
    try {
      await addPantryItem(newItem);
      await fetchPantryItems();
      setAddItemDialogOpen(false);
      setNewItem({ ingredient_name: '', quantity: '', unit: '', expiration_date: '' });
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  const handleDeleteItem = async (itemName) => {
    try {
      await deletePantryItem(itemName);
      await fetchPantryItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handlePreferenceChange = (category, value) => {
    setPreferences((prev) => {
      const current = prev[category] || [];
      const updated = current.includes(value)
        ? current.filter((item) => item !== value)
        : [...current, value];
      return { ...prev, [category]: updated };
    });
  };

  const isExpiringSoon = (expirationDate) => {
    if (!expirationDate) return false;
    const today = new Date();
    const expDate = new Date(expirationDate);
    const daysUntilExpiry = Math.ceil((expDate - today) / (1000 * 60 * 60 * 24));
    return daysUntilExpiry <= 3 && daysUntilExpiry >= 0;
  };

  return (
    <Box sx={{ display: 'flex', height: 'calc(100vh - 64px)', bgcolor: '#fafafa' }}>
      {/* Left Sidebar - Notion Style */}
      <Box
        sx={{
          width: 280,
          flexShrink: 0,
          bgcolor: '#ffffff',
          borderRight: '1px solid #e0e0e0',
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
        }}
      >
        {/* Sidebar Header */}
        <Box sx={{ p: 2.5, borderBottom: '1px solid #f0f0f0' }}>
          <Typography variant="body2" sx={{ fontSize: '13px', fontWeight: 600, color: '#37352f', mb: 0.5 }}>
            Your Kitchen
          </Typography>
          <Typography variant="caption" sx={{ fontSize: '11px', color: '#9b9a97' }}>
            Manage ingredients & preferences
          </Typography>
        </Box>

        {/* Scrollable Content */}
        <Box sx={{ flexGrow: 1, overflow: 'auto', p: 2 }}>
          <Stack spacing={3}>
            {/* Preferences Section */}
            <Box>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1.5, px: 1 }}>
                <SettingsIcon sx={{ fontSize: 16, color: '#9b9a97' }} />
                <Typography variant="body2" sx={{ fontSize: '12px', fontWeight: 600, color: '#37352f', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
                  Preferences
                </Typography>
              </Box>

              <Stack spacing={1.5}>
                {/* Skill Level */}
                <Box sx={{ px: 1 }}>
                  <Typography variant="caption" sx={{ fontSize: '11px', fontWeight: 500, color: '#787774', mb: 0.5, display: 'block' }}>
                    Skill Level
                  </Typography>
                  <FormControl fullWidth size="small">
                    <Select
                      value={preferences.skill_level}
                      onChange={(e) => setPreferences({ ...preferences, skill_level: e.target.value })}
                      sx={{
                        fontSize: '13px',
                        bgcolor: '#f7f6f3',
                        '& .MuiOutlinedInput-notchedOutline': { border: 'none' },
                        '&:hover': { bgcolor: '#f0eeeb' },
                        borderRadius: 1,
                      }}
                    >
                      <MenuItem value="beginner" sx={{ fontSize: '13px' }}>Beginner</MenuItem>
                      <MenuItem value="intermediate" sx={{ fontSize: '13px' }}>Intermediate</MenuItem>
                      <MenuItem value="advanced" sx={{ fontSize: '13px' }}>Advanced</MenuItem>
                    </Select>
                  </FormControl>
                </Box>

                {/* Dietary Restrictions */}
                <Box sx={{ px: 1 }}>
                  <Typography variant="caption" sx={{ fontSize: '11px', fontWeight: 500, color: '#787774', mb: 0.5, display: 'block' }}>
                    Dietary
                  </Typography>
                  <Stack spacing={0.5}>
                    {['vegetarian', 'vegan', 'gluten-free', 'dairy-free'].map((diet) => (
                      <Box
                        key={diet}
                        onClick={() => handlePreferenceChange('dietary_restrictions', diet)}
                        sx={{
                          display: 'flex',
                          alignItems: 'center',
                          gap: 1,
                          p: 0.75,
                          borderRadius: 0.75,
                          cursor: 'pointer',
                          bgcolor: preferences.dietary_restrictions.includes(diet) ? '#f7f6f3' : 'transparent',
                          '&:hover': { bgcolor: '#f7f6f3' },
                          transition: 'background-color 0.15s',
                        }}
                      >
                        <Box
                          sx={{
                            width: 14,
                            height: 14,
                            borderRadius: 0.5,
                            border: '1.5px solid',
                            borderColor: preferences.dietary_restrictions.includes(diet) ? '#4caf50' : '#d1d0ce',
                            bgcolor: preferences.dietary_restrictions.includes(diet) ? '#4caf50' : 'transparent',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            transition: 'all 0.15s',
                          }}
                        >
                          {preferences.dietary_restrictions.includes(diet) && (
                            <Box sx={{ width: 6, height: 3, borderLeft: '1.5px solid white', borderBottom: '1.5px solid white', transform: 'rotate(-45deg)', mb: 0.3 }} />
                          )}
                        </Box>
                        <Typography variant="body2" sx={{ fontSize: '13px', color: '#37352f' }}>
                          {diet.charAt(0).toUpperCase() + diet.slice(1).replace('-', ' ')}
                        </Typography>
                      </Box>
                    ))}
                  </Stack>
                </Box>

                {/* Allergies */}
                <Box sx={{ px: 1 }}>
                  <Typography variant="caption" sx={{ fontSize: '11px', fontWeight: 500, color: '#787774', mb: 0.5, display: 'block' }}>
                    Allergies
                  </Typography>
                  <Stack spacing={0.5}>
                    {['nuts', 'shellfish', 'eggs', 'soy'].map((allergy) => (
                      <Box
                        key={allergy}
                        onClick={() => handlePreferenceChange('allergies', allergy)}
                        sx={{
                          display: 'flex',
                          alignItems: 'center',
                          gap: 1,
                          p: 0.75,
                          borderRadius: 0.75,
                          cursor: 'pointer',
                          bgcolor: preferences.allergies.includes(allergy) ? '#f7f6f3' : 'transparent',
                          '&:hover': { bgcolor: '#f7f6f3' },
                          transition: 'background-color 0.15s',
                        }}
                      >
                        <Box
                          sx={{
                            width: 14,
                            height: 14,
                            borderRadius: 0.5,
                            border: '1.5px solid',
                            borderColor: preferences.allergies.includes(allergy) ? '#4caf50' : '#d1d0ce',
                            bgcolor: preferences.allergies.includes(allergy) ? '#4caf50' : 'transparent',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            transition: 'all 0.15s',
                          }}
                        >
                          {preferences.allergies.includes(allergy) && (
                            <Box sx={{ width: 6, height: 3, borderLeft: '1.5px solid white', borderBottom: '1.5px solid white', transform: 'rotate(-45deg)', mb: 0.3 }} />
                          )}
                        </Box>
                        <Typography variant="body2" sx={{ fontSize: '13px', color: '#37352f' }}>
                          {allergy.charAt(0).toUpperCase() + allergy.slice(1)}
                        </Typography>
                      </Box>
                    ))}
                  </Stack>
                </Box>
              </Stack>
            </Box>

            {/* Pantry Section */}
            <Box>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1.5, px: 1 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <KitchenIcon sx={{ fontSize: 16, color: '#9b9a97' }} />
                  <Typography variant="body2" sx={{ fontSize: '12px', fontWeight: 600, color: '#37352f', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
                    Pantry
                  </Typography>
                </Box>
                <Chip 
                  label={pantryItems.length} 
                  size="small" 
                  sx={{ 
                    height: 18, 
                    fontSize: '11px', 
                    fontWeight: 500,
                    bgcolor: '#f7f6f3',
                    color: '#37352f',
                    '& .MuiChip-label': { px: 1 }
                  }} 
                />
              </Box>

              <Button
                startIcon={<AddIcon sx={{ fontSize: 16 }} />}
                fullWidth
                onClick={() => setAddItemDialogOpen(true)}
                sx={{
                  justifyContent: 'flex-start',
                  textTransform: 'none',
                  fontSize: '13px',
                  fontWeight: 500,
                  color: '#37352f',
                  bgcolor: 'transparent',
                  border: '1px dashed #d1d0ce',
                  borderRadius: 1,
                  py: 0.75,
                  mb: 1,
                  '&:hover': {
                    bgcolor: '#f7f6f3',
                    borderColor: '#9b9a97',
                  },
                }}
              >
                Add ingredient
              </Button>

              {pantryItems.length === 0 ? (
                <Box sx={{ textAlign: 'center', py: 3, px: 2 }}>
                  <Typography variant="caption" sx={{ fontSize: '12px', color: '#9b9a97' }}>
                    No ingredients yet
                  </Typography>
                </Box>
              ) : (
                <Stack spacing={0.5}>
                  {pantryItems.map((item) => (
                    <Box
                      key={item.ingredient_name}
                      sx={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                        p: 1,
                        borderRadius: 0.75,
                        '&:hover': {
                          bgcolor: '#f7f6f3',
                          '& .delete-icon': { opacity: 1 },
                        },
                        transition: 'background-color 0.15s',
                      }}
                    >
                      <Box sx={{ flexGrow: 1, minWidth: 0 }}>
                        <Typography 
                          variant="body2" 
                          sx={{ 
                            fontSize: '13px', 
                            color: '#37352f',
                            fontWeight: 500,
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap',
                          }}
                        >
                          {item.ingredient_name}
                        </Typography>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5, mt: 0.25 }}>
                          <Typography variant="caption" sx={{ fontSize: '11px', color: '#9b9a97' }}>
                            {item.quantity} {item.unit}
                          </Typography>
                          {isExpiringSoon(item.expiration_date) && (
                            <>
                              <Typography variant="caption" sx={{ fontSize: '11px', color: '#9b9a97' }}>â€¢</Typography>
                              <Typography variant="caption" sx={{ fontSize: '11px', color: '#ff9800', fontWeight: 500 }}>
                                Expires soon
                              </Typography>
                            </>
                          )}
                        </Box>
                      </Box>
                      <IconButton
                        className="delete-icon"
                        size="small"
                        onClick={() => handleDeleteItem(item.ingredient_name)}
                        sx={{
                          opacity: 0,
                          transition: 'opacity 0.15s',
                          width: 24,
                          height: 24,
                          '&:hover': {
                            bgcolor: alpha('#f44336', 0.1),
                          },
                        }}
                      >
                        <DeleteIcon sx={{ fontSize: 16, color: '#9b9a97' }} />
                      </IconButton>
                    </Box>
                  ))}
                </Stack>
              )}
            </Box>
          </Stack>
        </Box>
      </Box>

      {/* Main Chat Area */}
      <Box
        sx={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
          bgcolor: '#ffffff',
        }}
      >
        {/* Messages Area */}
        <Box
          sx={{
            flexGrow: 1,
            overflow: 'auto',
            p: 3,
          }}
        >
          <List sx={{ maxWidth: 900, mx: 'auto' }}>
            {messages.map((message, index) => (
              <ListItem
                key={index}
                sx={{
                  flexDirection: 'column',
                  alignItems: message.role === 'user' ? 'flex-end' : 'flex-start',
                  mb: 2,
                  px: 0,
                }}
              >
                <Box
                  sx={{
                    display: 'flex',
                    gap: 1.5,
                    alignItems: 'flex-start',
                    maxWidth: '85%',
                    flexDirection: message.role === 'user' ? 'row-reverse' : 'row',
                  }}
                >
                  <Avatar
                    sx={{
                      bgcolor: message.role === 'user' 
                        ? theme.palette.secondary.main 
                        : theme.palette.primary.main,
                      width: 36,
                      height: 36,
                    }}
                  >
                    {message.role === 'user' ? <PersonIcon fontSize="small" /> : <SmartToyIcon fontSize="small" />}
                  </Avatar>
                  <Box
                    sx={{
                      p: 2,
                      borderRadius: 2,
                      bgcolor: message.role === 'user' 
                        ? alpha(theme.palette.secondary.main, 0.1)
                        : 'background.paper',
                      border: 1,
                      borderColor: message.role === 'user' 
                        ? alpha(theme.palette.secondary.main, 0.2)
                        : 'divider',
                    }}
                  >
                    <Typography
                      variant="body1"
                      sx={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}
                    >
                      {message.content}
                    </Typography>

                    {/* Display recipe recommendations if available */}
                    {message.data?.top_3_recommendations && (
                      <Box sx={{ mt: 2, display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                        {message.data.top_3_recommendations.map((recipe, idx) => (
                          <Chip
                            key={idx}
                            label={recipe.title}
                            color="primary"
                            variant="outlined"
                          />
                        ))}
                      </Box>
                    )}
                  </Box>
                </Box>
              </ListItem>
            ))}
            {loading && (
              <ListItem sx={{ justifyContent: 'center', px: 0 }}>
                <CircularProgress size={24} />
              </ListItem>
            )}
            <div ref={messagesEndRef} />
          </List>
        </Box>

        {/* Input Area */}
        <Box
          sx={{
            p: 3,
            bgcolor: 'background.paper',
            borderTop: 1,
            borderColor: 'divider',
          }}
        >
          <Box sx={{ display: 'flex', gap: 1.5, maxWidth: 900, mx: 'auto' }}>
            <TextField
              fullWidth
              multiline
              maxRows={4}
              placeholder="Ask me about recipes, ingredients, or cooking tips..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              disabled={loading}
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  borderRadius: 3,
                },
              }}
            />
            <IconButton
              color="primary"
              onClick={handleSend}
              disabled={!input.trim() || loading}
              sx={{
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': {
                  bgcolor: 'primary.dark',
                },
                '&:disabled': {
                  bgcolor: alpha(theme.palette.primary.main, 0.3),
                },
                width: 48,
                height: 48,
              }}
            >
              <SendIcon />
            </IconButton>
          </Box>
        </Box>
      </Box>

      {/* Permanent Sidebar */}
      <Box
        sx={{
          width: 380,
          flexShrink: 0,
          bgcolor: 'background.paper',
          borderLeft: 1,
          borderColor: 'divider',
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
        }}
      >
        {/* Sidebar Header */}
        <Box sx={{ p: 3, borderBottom: 1, borderColor: 'divider' }}>
          <Typography variant="h6" fontWeight={600} gutterBottom>
            Settings & Pantry
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Manage your preferences and ingredients
          </Typography>
        </Box>

        {/* Scrollable Content */}
        <Box sx={{ flexGrow: 1, overflow: 'auto', p: 3 }}>
          <Stack spacing={3}>
            {/* Preferences Section */}
            <Card variant="outlined" sx={{ borderRadius: 2, overflow: 'hidden' }}>
              <Box sx={{ p: 2, bgcolor: alpha(theme.palette.primary.main, 0.05), display: 'flex', alignItems: 'center', gap: 1 }}>
                <SettingsIcon color="primary" fontSize="small" />
                <Typography variant="subtitle1" fontWeight={600}>
                  Preferences
                </Typography>
              </Box>
              <CardContent>
                <Stack spacing={2.5}>
                  {/* Skill Level */}
                  <FormControl fullWidth size="small">
                    <InputLabel>Skill Level</InputLabel>
                    <Select
                      value={preferences.skill_level}
                      label="Skill Level"
                      onChange={(e) => setPreferences({ ...preferences, skill_level: e.target.value })}
                    >
                      <MenuItem value="beginner">Beginner</MenuItem>
                      <MenuItem value="intermediate">Intermediate</MenuItem>
                      <MenuItem value="advanced">Advanced</MenuItem>
                    </Select>
                  </FormControl>

                  {/* Dietary Restrictions */}
                  <Box>
                    <Typography variant="body2" fontWeight={600} gutterBottom>
                      Dietary Restrictions
                    </Typography>
                    <FormGroup>
                      {['vegetarian', 'vegan', 'gluten-free', 'dairy-free'].map((diet) => (
                        <FormControlLabel
                          key={diet}
                          control={
                            <Checkbox
                              size="small"
                              checked={preferences.dietary_restrictions.includes(diet)}
                              onChange={() => handlePreferenceChange('dietary_restrictions', diet)}
                            />
                          }
                          label={<Typography variant="body2">{diet.charAt(0).toUpperCase() + diet.slice(1).replace('-', ' ')}</Typography>}
                        />
                      ))}
                    </FormGroup>
                  </Box>

                  {/* Allergies */}
                  <Box>
                    <Typography variant="body2" fontWeight={600} gutterBottom>
                      Allergies
                    </Typography>
                    <FormGroup>
                      {['nuts', 'shellfish', 'eggs', 'soy', 'wheat'].map((allergy) => (
                        <FormControlLabel
                          key={allergy}
                          control={
                            <Checkbox
                              size="small"
                              checked={preferences.allergies.includes(allergy)}
                              onChange={() => handlePreferenceChange('allergies', allergy)}
                            />
                          }
                          label={<Typography variant="body2">{allergy.charAt(0).toUpperCase() + allergy.slice(1)}</Typography>}
                        />
                      ))}
                    </FormGroup>
                  </Box>
                </Stack>
              </CardContent>
            </Card>

            {/* Pantry Section */}
            <Card variant="outlined" sx={{ borderRadius: 2, overflow: 'hidden' }}>
              <Box sx={{ p: 2, bgcolor: alpha(theme.palette.primary.main, 0.05), display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <KitchenIcon color="primary" fontSize="small" />
                  <Typography variant="subtitle1" fontWeight={600}>
                    Pantry
                  </Typography>
                  <Chip label={pantryItems.length} size="small" color="primary" />
                </Box>
              </Box>
              <CardContent>
                <Button
                  variant="contained"
                  startIcon={<AddIcon />}
                  fullWidth
                  onClick={() => setAddItemDialogOpen(true)}
                  sx={{ mb: 2, borderRadius: 2 }}
                >
                  Add Item
                </Button>

                {pantryItems.length === 0 ? (
                  <Box sx={{ textAlign: 'center', py: 4 }}>
                    <Typography variant="body2" color="text.secondary">
                      No items in pantry
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      Add ingredients to get started
                    </Typography>
                  </Box>
                ) : (
                  <Stack spacing={1.5}>
                    {pantryItems.map((item) => (
                      <Box
                        key={item.ingredient_name}
                        sx={{
                          p: 1.5,
                          border: 1,
                          borderColor: 'divider',
                          borderRadius: 1.5,
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center',
                          '&:hover': {
                            bgcolor: alpha(theme.palette.primary.main, 0.02),
                          },
                        }}
                      >
                        <Box sx={{ flexGrow: 1 }}>
                          <Typography variant="body2" fontWeight={500}>
                            {item.ingredient_name}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {item.quantity} {item.unit}
                          </Typography>
                          {item.expiration_date && isExpiringSoon(item.expiration_date) && (
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5, mt: 0.5 }}>
                              <WarningAmberIcon sx={{ fontSize: 14, color: 'warning.main' }} />
                              <Typography variant="caption" color="warning.main">
                                Expires soon
                              </Typography>
                            </Box>
                          )}
                        </Box>
                        <IconButton
                          size="small"
                          color="error"
                          onClick={() => handleDeleteItem(item.ingredient_name)}
                        >
                          <DeleteIcon fontSize="small" />
                        </IconButton>
                      </Box>
                    ))}
                  </Stack>
                )}
              </CardContent>
            </Card>
          </Stack>
        </Box>
      </Box>

    {/* Add Item Dialog */}
    <Dialog open={addItemDialogOpen} onClose={() => setAddItemDialogOpen(false)} maxWidth="sm" fullWidth>
      <DialogTitle>Add Pantry Item</DialogTitle>
      <DialogContent>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
          <TextField
            label="Ingredient Name"
            value={newItem.ingredient_name}
            onChange={(e) => setNewItem({ ...newItem, ingredient_name: e.target.value })}
            fullWidth
          />
          <TextField
            label="Quantity"
            type="number"
            value={newItem.quantity}
            onChange={(e) => setNewItem({ ...newItem, quantity: e.target.value })}
            fullWidth
          />
          <TextField
            label="Unit"
            value={newItem.unit}
            onChange={(e) => setNewItem({ ...newItem, unit: e.target.value })}
            placeholder="e.g., cups, pieces, grams"
            fullWidth
          />
          <TextField
            label="Expiration Date"
            type="date"
            value={newItem.expiration_date}
            onChange={(e) => setNewItem({ ...newItem, expiration_date: e.target.value })}
            InputLabelProps={{ shrink: true }}
            fullWidth
          />
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setAddItemDialogOpen(false)}>Cancel</Button>
        <Button onClick={handleAddItem} variant="contained">Add</Button>
      </DialogActions>
    </Dialog>
  </Box>
  );
};

export default ChatInterface;
