import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box, IconButton, Tooltip } from '@mui/material';
import { Link as RouterLink, useLocation } from 'react-router-dom';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import ChatIcon from '@mui/icons-material/Chat';
import SearchIcon from '@mui/icons-material/Search';
import MenuIcon from '@mui/icons-material/Menu';
import MenuOpenIcon from '@mui/icons-material/MenuOpen';

function Navbar({ sidebarOpen, setSidebarOpen }) {
  const location = useLocation();

  return (
    <AppBar 
      position="static" 
      elevation={0}
      sx={{ 
        backgroundColor: '#ffffff',
        borderBottom: '1px solid #e5e7eb',
      }}
    >
      <Toolbar sx={{ gap: 2 }}>
        {/* Sidebar Toggle */}
        <Tooltip title={sidebarOpen ? "Hide sidebar" : "Show sidebar"}>
          <IconButton 
            onClick={() => setSidebarOpen(!sidebarOpen)}
            sx={{ color: '#6b7280' }}
          >
            {sidebarOpen ? <MenuOpenIcon /> : <MenuIcon />}
          </IconButton>
        </Tooltip>

        {/* Logo */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
          <Box
            sx={{
              width: 36,
              height: 36,
              borderRadius: 1.5,
              backgroundColor: '#10b981',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <RestaurantIcon sx={{ color: '#ffffff', fontSize: 20 }} />
          </Box>
          <Typography 
            variant="h6" 
            sx={{ 
              fontWeight: 700, 
              color: '#1f2937',
              display: { xs: 'none', sm: 'block' }
            }}
          >
            Leftovr
          </Typography>
        </Box>

        {/* Spacer */}
        <Box sx={{ flexGrow: 1 }} />

        {/* Navigation Links */}
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Button
            component={RouterLink}
            to="/"
            startIcon={<ChatIcon />}
            sx={{
              textTransform: 'none',
              fontWeight: 500,
              color: location.pathname === '/' ? '#10b981' : '#6b7280',
              backgroundColor: location.pathname === '/' ? '#10b98110' : 'transparent',
              '&:hover': {
                backgroundColor: location.pathname === '/' ? '#10b98120' : '#f3f4f6',
              },
            }}
          >
            Chat
          </Button>
          <Button
            component={RouterLink}
            to="/recipes"
            startIcon={<SearchIcon />}
            sx={{
              textTransform: 'none',
              fontWeight: 500,
              color: location.pathname === '/recipes' ? '#10b981' : '#6b7280',
              backgroundColor: location.pathname === '/recipes' ? '#10b98110' : 'transparent',
              '&:hover': {
                backgroundColor: location.pathname === '/recipes' ? '#10b98120' : '#f3f4f6',
              },
            }}
          >
            Recipes
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
