import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  Chip,
  IconButton,
  Tooltip,
  CircularProgress,
  Alert,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import MenuIcon from '@mui/icons-material/Menu';
import RestaurantMenuIcon from '@mui/icons-material/RestaurantMenu';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

function RecipeSearch({ sidebarOpen, setSidebarOpen }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setError('Please enter a search query');
      return;
    }

    setLoading(true);
    setError('');
    setRecipes([]);

    try {
      const response = await axios.post(`${API_BASE_URL}/recipes/search`, {
        query: searchQuery,
        limit: 10
      });

      setRecipes(response.data.recipes || []);

      if (response.data.recipes?.length === 0) {
        setError('No recipes found. Try a different search term.');
      }
    } catch (err) {
      setError('Failed to search recipes: ' + (err.response?.data?.detail || err.message));
      console.error('Error searching recipes:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Recipe Search
      </Typography>

      {/* Search Bar */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box display="flex" gap={2}>
          <TextField
            fullWidth
            label="Search for recipes..."
            placeholder="e.g., chicken pasta, vegetarian soup, chocolate cake"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={handleKeyPress}
            disabled={loading}
          />
          <Button
            variant="contained"
            startIcon={loading ? <CircularProgress size={20} /> : <SearchIcon />}
            onClick={handleSearch}
            disabled={loading}
            sx={{ minWidth: 120 }}
          >
            {loading ? 'Searching...' : 'Search'}
          </Button>
        </Box>
      </Paper>

      {/* Error Message */}
      {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

      {/* Results */}
      {recipes.length > 0 && (
        <>
          <Typography variant="h6" gutterBottom>
            Found {recipes.length} recipes
          </Typography>
          <Grid container spacing={3}>
            {recipes.map((recipe, index) => (
              <Grid item xs={12} sm={6} md={4} key={recipe.id || index}>
                <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography variant="h6" component="div" gutterBottom>
                      {recipe.title || recipe.name}
                    </Typography>

                    {recipe.description && (
                      <Typography variant="body2" color="text.secondary" paragraph>
                        {recipe.description.length > 150
                          ? `${recipe.description.substring(0, 150)}...`
                          : recipe.description}
                      </Typography>
                    )}

                    {recipe.ingredients && (
                      <Box sx={{ mt: 2 }}>
                        <Typography variant="subtitle2" gutterBottom>
                          Ingredients:
                        </Typography>
                        <Box display="flex" flexWrap="wrap" gap={0.5}>
                          {recipe.ingredients.slice(0, 5).map((ing, idx) => (
                            <Chip
                              key={idx}
                              label={ing}
                              size="small"
                              variant="outlined"
                            />
                          ))}
                          {recipe.ingredients.length > 5 && (
                            <Chip
                              label={`+${recipe.ingredients.length - 5} more`}
                              size="small"
                              variant="outlined"
                            />
                          )}
                        </Box>
                      </Box>
                    )}

                    {recipe.score && (
                      <Typography variant="caption" color="text.secondary" sx={{ mt: 2, display: 'block' }}>
                        Match score: {(recipe.score * 100).toFixed(1)}%
                      </Typography>
                    )}
                  </CardContent>

                  <CardActions>
                    <Button size="small" color="primary">
                      View Recipe
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </>
      )}

      {/* Loading State */}
      {loading && (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight={200}>
          <CircularProgress />
        </Box>
      )}
    </Box>
  );
}

export default RecipeSearch;
